import React from "react";
import * as Icon from "react-icons/fi";
import Checkbox from "react-custom-checkbox";

const App = () => {
  return (
    <>
      <h4>Default</h4>
      <Checkbox />
      <h4>Using Custom Icon</h4>
      <Checkbox
        icon={<Icon.FiCheck color="#174A41" size={14} />}
        name="my-input"
        checked={true}
        onChange={(value) => {
          let p = {
            isTrue: value,
          };
          return alert(value);
        }}
        borderColor="#D7C629"
        style={{ cursor: "pointer" }}
        labelStyle={{ userSelect: "none" }}
        label="Have you started using it?"
      />
      <h4>Using Image Icon</h4>
      <Checkbox
        icon={<img src="../public/check.png" style={{ width: 18 }} alt="" />}
        borderColor="#D7C629"
        borderRadius={10}
        size={20}
        label="Coooool right?"
      />
      <h4>More Styling</h4>
      <Checkbox
        icon={
          <div style={{ display: "flex", flex: 1, backgroundColor: "#174A41" }}>
            <Icon.FiCheck color="white" size={18} />
          </div>
        }
        borderColor="#174A41"
        // borderWidth={0}
        borderRadius={10}
        style={{ overflow: "hidden" }}
        size={20}
        label="Coooool right?"
      />
    </>
  );
};

export default App;
